mapper.select:

select t2.no, t2.title, t2.contents, t2.reg_date as regDate, t2.category_no as categoryNo
from category t1, post t2
where t1.no=t2.category_no
and t1.blog_id = #{blogId }
and t1.no = #{categoryNo }
order by t2.no desc

choose.otherwise:

select t2.no, t2.title, t2.contents, t2.reg_date as regDate, t2.category_no as categoryNo
from category t1, post t2
where t1.no=t2.category_no
and t1.blog_id = #{blogId }
and t1.no = #{categoryNo }
and t2.no = #{postNo }
order by t2.no desc
limit 1

mapper.update:

update blog
set title = #{title },
    logo = #{logo}
where id = #{id }
